# .colList ----
.colList <- function(mat,col){
  nrow = nrow(mat)
  ncol = ncol(mat)
  color_matrix <- matrix(NA, nrow = nrow, ncol = ncol)
  num_colors <- length(col)
  for (i in 1:nrow) {
    for (j in 1:ncol) {
      index <- ((i + j - 2) %% num_colors) + 1
      if(mat[i,j]==" "){
        color_matrix[i, j] <- "#F2F2F2"
      }
      else{
        color_matrix[i, j] <- col[index]
      }
    }
  }
  return(color_matrix)
}
